<!DOCTYPE html>
<html lang="zh">

<head>
  <style>
    /* 全局样式 */
    body {
      background-color: #EFF0F2;
    }

    /* 标题字体的样式 */
  h2 {
      margin-top: 30px;
      font-size: 30px;
      font-weight: bold;
      text-shadow: -2px -2px 5px #FFFFFF, 2px 2px 5px #B3B3B3;
    }

    /* 按钮和输入框的基本样式 */
    button,
    input {
    	 background-color: #EFF0F4;
      margin: 5px;
      border: 0.1px solid #FFFFFF;
      padding: 5px 10px;
      font-size: 14px;
      line-height: 1.4;
      box-shadow: inset -2px -2px 5px #FFFFFF, inset 2px 2px 5px #B3B3B3;
      border-radius: 6px;
      transition: all 0.3s ease;
    }

    /* 特定元素的样式 */
    #A,
    #basic-addon1,
    #replaceSpan1 {
      background-color: #EFF0F4;
      padding: 5px;
      border: 0.1px solid #FFFFFF;
      box-shadow: -1px -1px 3px #FFFFFF, 1px 1px 3px #B3B3B3;
      border-radius: 6px;
    }

    /* 按钮和输入按钮的样式 */
    button,
    input[type="button"] {
      outline: none;
      height: 30px;
      background: #EFF0F4;
      border: 0.1px solid #FFFFFF;
      box-shadow: -2px -2px 5px #FFFFFF, 2px 2px 5px #B3B3B3;
      border-radius: 6px;
    }

    /* 鼠标悬停时的样式 */
    button:hover,
    input[type="button"]:hover {
    	 border: 1px solid #FFFFFF;
      box-shadow: inset -2px -2px 5px #FFFFFF, inset 2px 2px 5px #B3B3B3;
      transform: scale(1);
    }

    /* 文本区域的样式 */
    textarea {
      background-color: #EFF0F4;
      padding: 5px;
      border: 2px solid #FAFAFA;
      box-shadow: inset 2px 2px 5px #B3B3B3, inset -2px -2px 10px #FFFFFF, 1px 1px 5px #B3B3B3, -1px -1px 5px #FFFFFF;
      border-radius: 6px;
    }

    /* 特定按钮的样式 */
  #bt7,
  #bt8 {
      width: 48.8%;
      border-radius: 6px;
      transition: all 0.3s ease;
    }

    /* 鼠标按下时的样式 */
  #bt7:active,
  #bt8:active {
  	   border: 1px solid #FFFFFF;
      box-shadow: inset -2px -2px 5px #FFFFFF, inset 2px 2px 5px #B3B3B3;
      transform: scale(1);
    }

    /* ID制作人的样式 */
  id {
  	   background-color: #EFF0F4;
  	   border: 0.1px solid #FFFFFF;
  	   padding: 5px;
  	   box-shadow: inset 1px 1px 3px #B3B3B3, inset -1px -1px 3px #FFFFFF;
  	   border-radius: 12px;">
    }
  </style>
</head>

<body>
  <div class="center">
    <!-- 链接示例 -->
    <div style="margin: 10px 0px 30px 0px; font-size: 15px; text-align: center;">
    <a href="https://www.xuhuhu.com/beautify/urlencode/" id="A">URL 解码</a>
    <a href="http://5ujq.com/" id="A">简繁转换</a>
    <a href="https://neumorphism.io/" id="A">CSS代码生成器</a></div>
    <div class="center"><center>
    <h2> 美化发现 </h2>
   </center>
  </div>
    <!-- 输入框 -->
    <textarea rows="10" id="input" onfocus="di()" placeholder="格式化发现请输入带 html 标签的发现内容"></textarea>
    <br />
    <br> 发现转换 </br>
    <!-- 按钮 -->
    <button onclick="getInput1(); dis()" id="bt1">格式化发现</button>
    <button onclick="getInput2(); dis()" id="bt2">发现格式互转</button>
    <br />
    <br> 发现排版 </br>
    <!-- 不同排版的按钮 -->
    <button onclick="getInput3(1);" id="bt3">一行四组</button>
    <button onclick="getInput3(2);" id="bt4">一行三组</button>
    <button onclick="getInput3(3);" id="bt5">一行两组</button>
    <button onclick="getInput3(4);" id="bt6">一行一组</button>
    <button onclick="getInput3(5);" id="bt7">排版必选项</button>
    <br />
    <br> 复制结果 </br>
    <!-- 复制结果的按钮 -->
    <button onclick="copyText()" id="bt8">一键复制</button>
    <p></p>
    <div class="center"><center>
    <h2> 正则表达式匹配替换 </h2>
   </center>
  </div>
    <!-- 输入框 -->
    <div class="form-group">
      <div class="col-sm-12">
        <textarea class="form-control" onmouseover="this.focus();" id="textSour" name="textSour" rows="10"
          placeholder="请输入要验证的文本"></textarea>
      </div>
    </div>
    <!-- 正则表达式等输入框 -->
    <div class="form-group">
      <div class="col-sm-12">
        <div class="input-group">
          <span class="input-group-addon" id="basic-addon1">正则表达式</span>
          <input type="text" class="form-control" id="textPattern" name="textPattern" placeholder="请输入自定义正则表达式"
            aria-describedby="basic-addon1" style="width: 65%;">
        </div>
      </div>
    </div>
    <!-- 其他选项 -->
    <div class="form-group">
      <div class="col-sm-2">
        <div class="input-group" style="margin-left: 20px;">
          <label class="checkbox" style="cursor: pointer;">
            <input type="checkbox" value="global" checked="checked" id="optionGlobal" name="optionGlobl">全局搜索</label>
        </div>
      </div>
      <div class="col-sm-2">
        <div class="input-group" style="margin-left: 20px;">
          <label class="checkbox" style="cursor: pointer;">
            <input type="checkbox" value="ignoreCase" id="optionIgnoreCase" name="optionIgnoreCase">忽略大小写</label>
        </div>
      </div>
      <div class="col-sm-8">
        <!-- 验证、清空、复制按钮 -->
        <input type="button" onclick="return onMatch();" value="验证匹配内容" class="btn btn-success">
        <input type="button" onclick="Empty();" value="清空输入框" class="btn btn-danger">
        <button onclick="copyText3()">一键复制</button>
      </div>
    </div>
    <!-- 结果展示 -->
    <div class="form-group">
      <div class="col-sm-12">
        <div id="textResult"></div>
        <textarea onmouseover="this.focus();" id="textMatchResult" class="form-control" rows="10" style="max-height: 2222px;"
          placeholder="得到验证后内容，内容为空为验证失败" readonly="readonly"></textarea>
      </div>
    </div>
    <div class="form-group">
      <div class="col-sm-4">
        <div class="input-group" style="width: 98%;">
          <span class="input-group-addon" id="replaceSpan1">替换文本</span>
          <input type="text" class="form-control" id="textReplace" name="textReplace" placeholder="请输入替换内容"
            aria-describedby="replaceSpan1" style="width: 70%;">
          <!-- 替换按钮 -->
          <button value="{\{page}}" onclick="pp(this.value)">page</button>
          <button value="." onclick="pp(this.value)">.</button>
          <button value="/" onclick="pp(this.value)">/</button>
          <button value="_" onclick="pp(this.value)">_</button>
          <button value="-" onclick="pp(this.value)">-</button>
          <button value="+" onclick="pp(this.value)">+</button>
          <button value="=" onclick="pp(this.value)">=</button>
          <button value="&" onclick="pp(this.value)">&</button>
          <button value="$" onclick="pp(this.value)">$</button>
        </div>
      </div>
      <div class="col-sm-2">
        <!-- 替换按钮 -->
        <div class="input-group">
          <button onclick="return onReplace()" class="btn btn-success">替换匹配内容</button>
          <button onclick="copyText2()">一键复制</button>
        </div>
      </div>
      <div class="col-sm-6">
      </div>
    </div>
    <div class="form-group">
      <div class="col-sm-12">
        <textarea readonly="readonly" id="textReplaceResult" class="form-control" rows="10" placeholder="此处为替换后内容"></textarea>
      </div>
    </div>
    <div class="center"><center style="margin: 30px 0px 10px 0px; font-size: 12px; text-align: center;">
      <id>
      源码：关耳；源码优化&UI：一程
      </id></center>
    </div>
</body>

<script>
  /* 自定义函数 */
  function $(selector) {
    return document.querySelector(selector);
  }

  function $$(selector) {
    return document.querySelectorAll(selector);
  }
</script>

<script>
var copyText = function() {
  var text = document.getElementById('input');
  // 选中文本
  text.select()
  // 复制
  document.execCommand('copy')
}
var copyText2 = function() {
  var text = document.getElementById('textReplaceResult');
  // 选中文本
  text.select()
  // 复制
  document.execCommand('copy')
}
var copyText3 = function() {
  var text = document.getElementById('textMatchResult');
  // 选中文本
  text.select()
  // 复制
  document.execCommand('copy')
}
</script>

<script>
function pp(a){
$("#textReplace").value+=a.replace(/\\/,'');
$("#textReplace").focus()
}
function dis(){
document.getElementById("bt1").disabled=true
}
function di(){
document.getElementById("bt1").disabled=false
}
function getInput1(){
text=$("#input").value;
list=text.match(/<a.*?href="([^"]+)".*?>([\s\S]+?)<\/a>/g);
html=""
if(list.length==0){
html="格式化失败！！！请检查是否为带html标签"
}else{
html=""
}
for(i in list){
a=list[i].match(/<a.*?href="([^"]+)".*?>([\s\S]+?)<\/a>/);
html+=a[2]+"::"+a[1]+"\n"
}
$("#input").value=html
}


function getInput2(){
text=$("#input").value;
if(text.match(/\&\&|::/g)){
text=text.replace(/\&\&/g,'\n');
list=text.match(/.*::.*/g);
html=[];
for(i=0;i<list.length;i++){
l=list[i].match(/(.*)::(.*)/);
title=l[1];url=l[2]
html.push({
title:title,
url:url,
style:{}
})
}
$("#input").value=JSON.stringify(html)
}else{
html="";
json=JSON.parse(text);
for(i in json){
html+=json[i].title+"::"+json[i].url+"\n"
}
$("#input").value=html
}
}
function getInput3(flag){
text=$("#input").value;
if(flag==1){
json=JSON.parse(text);
for(i in json){
json[i].style.layout_flexBasisPercent=0.2
}
}else if(flag==2){
json=JSON.parse(text);
for(i in json){
json[i].style.layout_flexBasisPercent=0.25
}
}else if(flag==3){
json=JSON.parse(text);
for(i in json){
json[i].style.layout_flexBasisPercent=0.4
}
}else if(flag==4){
json=JSON.parse(text);
for(i in json){
json[i].style.layout_flexBasisPercent=1
}
}else if(flag==5){
json=JSON.parse(text);
for(i in json){
json[i].style.layout_flexGrow=1
}
}
$("#input").value=JSON.stringify(json)
}
function setVisible(idElement, visible) {
  var obj = document.getElementById(idElement);
  obj.style.visibility = visible? "visible" : "hidden";
}
function isValidFields() {
  var textSour = document.getElementById("textSour");
  if (null == textSour.value || textSour.value.length < 1) {
    textSour.focus();
    JsonsMessageBox($("#textSour"), "请输入待匹配文本");
    return false;
  }
  var textPattern = document.getElementById("textPattern");
  if (null == textPattern.value || textPattern.value.length < 1) {
    textPattern.focus();
    JsonsMessageBox($("#textPattern"), "请输入正则表达式");
    return false;
  }
  return true;
}
function buildRegex() {
  var op = "";
  if (document.getElementById("optionGlobal").checked) op = "g";
  if (document.getElementById("optionIgnoreCase").checked) op = op + "i";
  return new RegExp(document.getElementById("textPattern").value, op);
}
function onMatch() {
  if (!isValidFields())
    return false;
  document.getElementById("textMatchResult").value = "";
  var regex = buildRegex();
  var result = document.getElementById("textSour").value.match(regex);
  if (null == result || 0 == result.length) {
    document.getElementById("textMatchResult").value = "（没有匹配）";
    return false;
  }
  if (document.getElementById("optionGlobal").checked) {
    var re="";
    var strResult = "共找到 " + result.length + " 处匹配：\r\n";
    document.getElementById("textResult").innerText=strResult;
    for (var i = 0; i < result.length; ++i) { re += result[i] + "\r\n"; }
    document.getElementById("textMatchResult").value = re;
  }
  else {
    document.getElementById("textMatchResult").value = "匹配位置：" + regex.lastIndex + "\r\n匹配结果："+result[0];
  }
  return true;
}
function onReplace() {
  var str = document.getElementById("textSour").value;
  var regex = buildRegex();
  document.getElementById("textReplaceResult").value = str.replace(regex, document.getElementById("textReplace").value);
}
function reset() {
  $("#textSour").value="";
  $("#textPattern").value="";
  $("#textMatchResult").value="";
  $("#textReplace").value="";
  $("#textReplaceResult").value="";
}
$("#regCommon a").click(function () {
  $("#textPattern").val($(this).attr("t"));
  onMatch();
});
function Empty() {
  $("#textSour").value="";
  $("#textMatchResult").value="";
  $("#textReplace").value="";
  $("#textReplaceResult").value="";
  $("#textSour").select();
}
</script>
</html>
