if(index.match(/\d+、\d+、/)){
reg=eval('/❗️'+index+'开始❗️[\\s\\S]+?❗️'+index+'结束❗️/g');
reg2=eval('/❗️'+index+'..❗️/g');
result=result.match(reg)?result.match(reg)[0]:'<br/>'
result.replace(reg2,'')}else{result=''}
</js>

style:
/*消除链接下划线*/
a{text-decoration: none;}


/*去除details小三角及边框*/
details summary::-webkit-details-marker { display:none; }
summary{outline:none;}


/*div样式〔渐变〕*/
.渐变{border: 1px solid; border-image: -webkit-linear-gradient(90deg,#6772FF, #51E2F7) 5 10;}


/*div样式〔按钮〕*/
.按钮{
/*背景颜色和字体颜色*/
background:#f2ecde; color:#622a1d;
/*边框大小及颜色(上，左，右，下)*/
border-top:2px   solid #dcdcdc;
border-left:2px   solid #dcdcdc;
border-right:2px   solid #a3a3a3;
border-bottom:2px   solid #a3a3a3;
/*阴影大小及颜色(下，右下，右)*/
box-shadow: 1px 0px 1px #404040;
/*文字与边框的距离(左，右)*/
padding-left:2px;
padding-right:2px;
/*圆角弧度*/
border-radius:10px;
/*宽与高*/
width:90%;
height:25px;
/*文字居中*/
text-align: center;
}


/*div样式〔背景〕*/
.背景{
/*背景颜色*/
background-color:#f2ecde;
/*圆角弧度*/
border-radius:15px;
/*边框大小及颜色*/
border:2px   solid #622a1d;
}

